mapper.select:
SELECT count(1)
FROM info_totalorder a
WHERE a.brandname = #{brandname} 

		AND a.seq != #{seq}

SELECT
	a.seq,
	a.totalid,
	a.tradeid,
	a.itemid,
	a.createdate,
	a.updatedate,
	a.stype
FROM info_totalorder a
WHERE 1 = 1

		AND a.seq = #{seq}


		AND a.totalid = #{totalid}


		AND a.tradeid = #{tradeid}


		AND a.stype = #{stype}


		AND a.itemid = #{itemid}

SELECT
	a.seq,
	a.totalid,
	a.tradeid,
	a.itemid,
	a.createdate,
	a.updatedate,
	a.stype
FROM info_totalorder a
WHERE 1 = 1

		AND a.seq = #{seq}


		AND a.totalid = #{totalid}


		AND a.tradeid = #{tradeid}


		AND a.stype = #{stype}


		AND a.itemid = #{itemid}

mapper.update:
UPDATE info_totalorder a
     SET a.updatedate = #{updatedate}

     	, a.totalid = #{totalid}


     	, a.tradeid = #{tradeid}


     	, a.itemid = #{itemid}


     	, a.createdate = #{createdate}


     	, a.stype = #{stype}

  WHERE a.seq = #{seq}

UPDATE info_totalorder a
     SET a.updatedate = #{updatedate}

     	, a.totalid = #{totalid}


     	, a.tradeid = #{tradeid}


     	, a.itemid = #{itemid}


     	, a.createdate = #{createdate}


     	, a.stype = #{stype}

  WHERE a.seq = #{seq}
