mapper.select:
SELECT count(1)
FROM info_trade a
WHERE a.brandname = #{brandname} 

		AND a.tradeid != #{tradeid}

mapper.update:
UPDATE info_trade a
     SET a.updatedate = #{updatedate}

     	, a.itemids = #{itemids}


     	, a.userids = #{userids}


     	, a.buyuserid = #{buyuserid}


     	, a.paydate = #{paydate}


     	, a.createdate = #{createdate}


     	, a.cost = #{cost}


     	, a.totalcost = #{totalcost}


     	, a.failure_msg = #{failure_msg}


     	, a.failuredate = #{failuredate}


     	, a.transfer = #{transfer}


     	, a.sstatus = #{sstatus}

  WHERE a.tradeid = #{tradeid}

UPDATE info_trade a
