mapper.select:
SELECT count(1)
FROM conf_config a
WHERE a.brandname = #{brandname} 

		AND a.seq != #{seq}

SELECT a.seq, a.ckey, a.cvalue, a.ctbname, a.cfield, a.cflag, a.remark, a.createdate, a.updatedate FROM conf_config a WHERE 1 = 1
AND a.seq = #{seq}
AND a.ckey = #{ckey}
AND a.cvalue = #{cvalue}
AND a.ctbname = #{ctbname}
AND a.cfield = #{cfield}

SELECT a.seq, a.ckey, a.cvalue, a.ctbname, a.cfield, a.cflag, a.remark, a.createdate, a.updatedate FROM conf_config a WHERE 1 = 1
AND a.seq = #{seq}
AND a.ckey = #{ckey}
AND a.cvalue = #{cvalue}
AND a.ctbname = #{ctbname}
AND a.cfield = #{cfield}

mapper.update:
UPDATE conf_config a
     SET a.updatedate = #{updatedate}

     	, a.ckey = #{ckey}


     	, a.cvalue = #{cvalue}


     	, a.ctbname = #{ctbname}


     	, a.cfield = #{cfield}


     	, a.cflag = #{cflag}


     	, a.remark = #{remark}


     	, a.createdate = #{createdate}

  WHERE a.seq = #{seq}

UPDATE conf_config a
     SET a.updatedate = #{updatedate}

     	, a.ckey = #{ckey}


     	, a.cvalue = #{cvalue}


     	, a.ctbname = #{ctbname}


     	, a.cfield = #{cfield}


     	, a.cflag = #{cflag}


     	, a.remark = #{remark}


     	, a.createdate = #{createdate}

  WHERE a.seq = #{seq}
