mapper.select:
SELECT count(1)
FROM conf_province a
WHERE a.brandname = #{brandname} 

		AND a.id != #{id}

mapper.update:
UPDATE conf_province a
     SET a.updatedate = #{updatedate}

     	, a.provinceid = #{provinceid}


     	, a.provincename = #{provincename}


     	, a.orderby = #{orderby}

  WHERE a.id = #{id}

