mapper.select:
SELECT count(1)
FROM log_token a
WHERE a.brandname = #{brandname} 

		AND a.seq != #{seq}

mapper.update:
UPDATE log_token a
     SET a.updatedate = #{updatedate}

     	, a.token = #{token}


     	, a.userid = #{userid}


     	, a.sflag = #{sflag}


     	, a.createdate = #{createdate}

  WHERE a.seq = #{seq}

UPDATE log_token a
     SET a.updatedate = #{updatedate}

     	, a.token = #{token}


     	, a.userid = #{userid}


     	, a.sflag = #{sflag}


     	, a.createdate = #{createdate}

  WHERE a.seq = #{seq}
